"use strict";exports.id=3831,exports.ids=[3831],exports.modules={83831:e=>{e.exports=JSON.parse('{"粉療":{"name":"粉疗","description":"在自己的回合时，可以使用1次。将自己的所有宝可梦恢复20HP。"},"反擊":{"name":"反击","description":"这只宝可梦在战斗场受到对手的宝可梦招式的伤害时，使用招式的宝可梦受到20点伤害。"},"硬殼盔甲":{"name":"硬壳盔甲","description":"这只宝可梦受到的招式伤害-10点。"},"芳香陷阱":{"name":"芳香陷阱","description":"若这只宝可梦在战斗场上，则在自己的回合时可使用1次。选择对手的备战区的1只基础宝可梦，与战斗宝可梦交换。"},"水手裡劍":{"name":"水手里剑","description":"在自己的回合时，可使用1次。对手的1只宝可梦受到20点伤害。"},"伏特充能":{"name":"伏特充能","description":"在自己的回合时，可使用1次。从自己的能量区抽出一个雷系能量附于这只宝可梦身上。"},"影子咒縛":{"name":"影子咒缚","description":"只要这只宝可梦在战斗场上，对手无法从手牌使出支援者卡。"},"引夢擺錘":{"name":"引梦摆锤","description":"在自己的回合时，可使用一次，掷1次硬币若为正面，则将对手的战斗宝可梦睡眠。"},"移形換影":{"name":"移形换影","description":"在自己的回合时，可使用1次。从自己的能量区抽出超能力能量，附于战斗场的超能力系宝可梦身上。"},"瓦斯漏氣":{"name":"瓦斯漏气","description":"若这只宝可梦在战斗上，则在自己的回合时可使用1次。将对手的战斗宝可梦中毒。"},"堅硬膜":{"name":"坚硬膜","description":"这只宝可梦受到招式的伤害-20点。"},"驅離":{"name":"驱离","description":"在自己的回合时，可使用1次。将对手的战斗宝可梦与备战宝可梦互换。「由对手选择放置于战斗场的宝可梦」。"},"資料掃描":{"name":"资料扫描","description":"在自己的回合时，可使用1次。从自己的牌库上方抽出1张，查看后恢复原状。"}}')}};